mapper.insert:
INSERT INTO detection_db.behavior_detection (
    detection_number, s_ip, d_ip, s_port, d_port, create_dt, modify_dt, action_type, policy_name, policy_info,
    pattern_1, pattern_2, pattern_3, dangerous, regexp_1, regexp_2, regexp_3, base_cnt, base_time
) VALUES (
    #{detection_number}, #{s_ip}, #{d_ip}, #{s_port}, #{d_port}, #{create_dt}, #{modify_dt}, #{action_type}, #{policy_name}, #{policy_info},
    #{pattern_1}, #{pattern_2}, #{pattern_3}, #{dangerous}, #{regexp_1}, #{regexp_2}, #{regexp_3}, #{base_cnt}, #{base_time}
)

mapper.update:
UPDATE detection_db.behavior_detection
SET s_ip = #{s_ip}, d_ip = #{d_ip}, s_port = #{s_port}, d_port = #{d_port}, create_dt = #{create_dt}, modify_dt = #{modify_dt},
    action_type = #{action_type}, policy_name = #{policy_name}, policy_info = #{policy_info}, pattern_1 = #{pattern_1},
    pattern_2 = #{pattern_2}, pattern_3 = #{pattern_3}, dangerous = #{dangerous}, regexp_1 = #{regexp_1},
    regexp_2 = #{regexp_2}, regexp_3 = #{regexp_3}, base_cnt = #{base_cnt}, base_time = #{base_time}
WHERE detection_number = #{detection_number}

mapper.select:
SELECT * FROM (
    SELECT ROW_NUMBER() OVER (ORDER BY detection_number) AS ROWNUM, behavior_detection.*
    FROM detection_db.behavior_detection
) AS paged
WHERE ROWNUM BETWEEN #{start} AND #{end}
