mapper.update:
UPDATE log_db.behavior_log_20240717
SET time = #{time}, s_ip = #{s_ip}, d_ip = #{d_ip}, s_port = #{s_port}, d_port = #{d_port},
    len = #{len}, pattern1 = #{pattern1}, pattern2 = #{pattern2}, pattern3 = #{pattern3}, 
    packet = #{packet}, base_cnt = #{base_cnt}, base_time = #{base_time}, action_type = #{action_type}
WHERE id = #{id}

mapper.select:
SELECT * FROM log_db.behavior_log_20240717 WHERE 1=1
AND id = #{id}
AND time LIKE CONCAT('%', #{time}, '%')
AND s_ip LIKE CONCAT('%', #{s_ip}, '%')
AND s_port = #{s_port}
AND d_ip LIKE CONCAT('%', #{d_ip}, '%')
AND d_port = #{d_port}
AND len = #{len}
AND pattern1 LIKE CONCAT('%', #{pattern1}, '%')
AND pattern2 LIKE CONCAT('%', #{pattern2}, '%')
AND pattern3 LIKE CONCAT('%', #{pattern3}, '%')
AND packet LIKE CONCAT('%', #{packet}, '%')
AND base_cnt = #{base_cnt}
AND base_time LIKE CONCAT('%', #{base_time}, '%')
AND action_type LIKE CONCAT('%', #{action_type}, '%')
